import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useLocation } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import { useEventForm } from './hooks/useEventForm';

const VENUE_TYPES = [
    "Stadiums", "Arenas", "Sports Complexes", "Gymnasiums", "Swimming Pools",
    "Athletic Tracks", "Tennis Courts", "Golf Courses", "Ice Rinks", "Cricket Grounds",
    "Baseball Fields", "Soccer Fields", "Basketball Courts", "Martial Arts Dojos", "Bowling Alleys"
];

const UpdateEventForm = () => {
    const [categories, setCategories] = useState([]);
    const [selectedImage1, setSelectImage1] = useState(null);
    const location = useLocation();
    const navigate = useNavigate();

    const { event, handleInput, setEvent } = useEventForm(location.state);

    const retrieveAllCategories = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/event/category/fetch/all");
            return response.data;
        } catch (error) {
            console.error("Erreur lors de la récupération des catégories:", error);
            toast.error("Impossible de charger les catégories. Veuillez réessayer.");
        }
    };

    useEffect(() => {
        const getAllCategories = async () => {
            const resCategory = await retrieveAllCategories();
            if (resCategory && resCategory.categories) {
                setCategories(resCategory.categories);
            }
        };

        getAllCategories();
    }, []);

    const validateForm = () => {
        if (!event.name || !event.description || !event.venueName || !event.location ||
            !event.noOfTickets || !event.startDate || !event.endDate || !event.ticketPrice ||
            !event.categoryId || !event.availableTickets) {
            toast.error("Veuillez remplir tous les champs obligatoires.");
            return false;
        }
        if (event.categoryId === "0") {
            toast.error("Veuillez sélectionner une catégorie.");
            return false;
        }
        if (!event.venueType) {
            toast.error("Veuillez sélectionner un type de lieu.");
            return false;
        }
        return true;
    };

    const saveEvent = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        const formData = new FormData();
        Object.keys(event).forEach(key => {
            if (key === 'startDate' || key === 'endDate') {
                formData.append(key, new Date(event[key]).getTime().toString());
            } else {
                formData.append(key, event[key]);
            }
        });

        if (selectedImage1) {
            formData.append("image", selectedImage1);
        }

        try {
            const response = await axios.put("http://localhost:8080/api/event/update", formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            if (response.data.success) {
                toast.success(response.data.responseMessage);
                setTimeout(() => navigate("/admin/event/all"), 2000);
            } else {
                toast.error(response.data.responseMessage);
            }
        } catch (error) {
            console.error("Erreur lors de la mise à jour de l'événement:", error);
            toast.error("Une erreur s'est produite lors de la mise à jour de l'événement. Veuillez réessayer.");
        }
    };

    return (
        <div>
            <div className="mt-2 d-flex aligns-items-center justify-content-center mb-4">
                <div className="card form-card shadow-lg" style={{ width: "60rem" }}>
                    <div className="container-fluid">
                        <div
                            className="card-header bg-color custom-bg-text mt-2 text-center"
                            style={{
                                borderRadius: "1em",
                                height: "45px",
                            }}
                        >
                            <h5 className="card-title">Mettre à jour l'événement</h5>
                        </div>
                        <div className="card-body text-color">
                            <form className="row g-3">
                                <div className="col-md-6 mb-3">
                                    <label htmlFor="name" className="form-label">
                                        <b>Titre de l'événement</b>
                                    </label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="name"
                                        name="name"
                                        onChange={handleInput}
                                        value={event.name}
                                        required
                                    />
                                </div>
                                <div className="col-md-6 mb-3">
                                    <label htmlFor="description" className="form-label">
